sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	gs.info('Business Rule Test: running for record ' + current.sys_id);

	var grSLA = new GlideRecord('task_sla');
	grSLA.addQuery('task', current.sys_id);
	grSLA.addQuery('stage', 'breached');
	grSLA.query();
	if (grSLA.next()) {
		gs.info('Business Rule Test: Breached SLA found!');
		if (current.priority < 4) {
			current.priority += 1;
			gs.info('Business Rule Test: Priority lowered to ' + current.priority);
		}
		current.update();
	}
})(current, previous);
