sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // 如果已经有编号，则不处理
    if (!current.tricket_number.nil() || !current.number.nil()) {
        return;
    }

    var prefix = "Nttca's";
    var today = new GlideDate();
    var yymmdd = today.getByFormat('yyMMdd');

    var gr = new GlideRecord('x_1799060_nttworkf_nttworkflow_task');
    gr.addQuery('tricket_number', 'STARTSWITH', prefix + yymmdd);
    gr.orderByDesc('tricket_number');
    gr.setLimit(1);
    gr.query();

    var num = 1;
    if (gr.next()) {
        var val = gr.tricket_number.toString();
        var last3 = val.slice(-3);
        var n = parseInt(last3, 10);
        if (!isNaN(n)) num = n + 1;
    }
    var newNum = num.toString().padStart(3, '0');
    current.tricket_number = prefix + yymmdd + newNum;
	current.number = prefix + yymmdd + newNum;

})(current, previous);
