sys_script_include.script:
var NttcaAutoNumberAjax = Class.create();
NttcaAutoNumberAjax.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    generateNumber: function() {
        var today = new GlideDate();
        var yymmdd = today.getByFormat('yyMMdd');
		var table = this.getParameter('sysparm_table');

        var gr = new GlideRecord(table);
        gr.addQuery('tricket_number', 'STARTSWITH', "Nttca's" + yymmdd);
        gr.orderByDesc('tricket_number');
        gr.setLimit(1);
        gr.query();
        var num = 1;
        if (gr.next()) {
            var val = gr.tricket_number.toString();
            var last3 = val.slice(-3);
            var n = parseInt(last3, 10);
            if (!isNaN(n)) num = n + 1;
        }
        var newNum = num.toString().padStart(3, '0');

        return "Nttca's" + yymmdd + newNum;
    }
});
